import React from 'react';
import { post } from 'axios';

class CustomerAdd extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            file: null,
            userName: '',
            birthDay: '',
            gender: '',
            job: '',
            fileName: ''
        }
    };

    addCustomer = () => {
        const url = '/api/customers';
        const formData = new FormData();
        formData.append('image', this.state.file);
        formData.append('name', this.state.userName);
        formData.append('birthday', this.state.birthDay);
        formData.append('gender', this.state.gender);
        formData.append('job', this.state.job);
        console.log(this.state.userName);
        const config = {
            headers: {
                'content-type': 'multipart/from-data'
            }
        }
        return post(url, formData, config);
    }

    handleFromSubmit = (e) => {
        e.preventDefault()
        this.addCustomer()
            .then((response) => {
                console.log(response.data);
            })
        // this.setState({
        //     file: null,
        //     userName: '',
        //     birthDay: '',
        //     gender: '',
        //     job: '',
        //     fileName: ''
        // })
        // window.location.reload();
    }

    handleiFileChange = (e) => {
        this.setState({
            file: e.target.files[0],
            fileName: e.target.value
        })
    }

    handleValuechange = (e) => {
        let nextState = {};
        nextState[e.target.name] = e.target.value;
        console.log(nextState, '')
        this.setState(nextState);
    }

    render() {
        return (
            <form onSubmit={this.handleFromSubmit}>
                <h1>고객 추가</h1>
                프로필 이미지: <input type="file" name="file" file={this.state.file} Value={this.state.fileName} onChange={this.state.handleiFileChange}/> <br/>
                이름: <input type="text" name="userName" Value={this.state.userName} onChange={this.state.handleValuechange} /> <br/>
                생년월일: <input type="text" name="birthDay" value={this.state.birthDay} onChange={this.state.handleValuechange} /> <br/>
                성별: <input type="text" name="gender" Value={this.state.gender} onChange={this.state.handleValuechange} /> <br/>
                직업: <input type="text" name="job" Value={this.state.job} onChange={this.state.handleValuechange}/> <br/>
                <button type="submit">추가하기</button>
            </form>
        );
    }
} 

export default CustomerAdd;
